 (2,'Gas Costa de Araujo','<ul>\r\n	<li><strong>Descripci&oacute;n</strong></li>\r\n</ul>\r\n\r\n<p>Gasoducto Lavalle &ndash; Costa y Redes domiciliarias</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Dimensiones</strong></li>\r\n</ul>\r\n\r\n<p>18km de gasoducto c/ estaciones reguladoras</p>\r\n\r\n<p>Red domiciliaria en todo el radio urbano del distrito</p>\r\n\r\n<ul>\r\n	<li><strong>Inversi&oacute;n:</strong>&nbsp;5.000.000</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Origen de los fondos:</strong>&nbsp;Nacional</li>\r\n</ul>\r\n',1),
 (3,'Gas El Chilcal - La Pega','<ul>\r\n	<li><strong>Descripci&oacute;n</strong></li>\r\n</ul>\r\n\r\n<p>Gasoducto y Redes domiciliarias</p>\r\n\r\n<ul>\r\n	<li><strong>Dimensiones</strong></li>\r\n</ul>\r\n\r\n<p>10km de tendido de gasoducto y redes domiciliarias</p>\r\n\r\n<ul>\r\n	<li><strong>Inversi&oacute;n:&nbsp;</strong>3.000.000</li>\r\n	<li><strong>Origen de los fondos:</strong>&nbsp;Provincial</li>\r\n</ul>\r\n',1),
 (4,'Gas Tres de Mayo y A. del Olvido','<ul>\r\n	<li><strong>Descripci&oacute;n</strong></li>\r\n</ul>\r\n\r\n<p>Gasoducto y Redes domiciliarias</p>\r\n\r\n<ul>\r\n	<li><strong>Dimensiones</strong></li>\r\n</ul>\r\n\r\n<p>10km de tendido de gasoducto y redes domiciliarias</p>\r\n\r\n<ul>\r\n	<li><strong>Inversi&oacute;n:&nbsp;</strong>3.000.000</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Origen de los fondos:&nbsp;</strong>Provincial</li>\r\n</ul>\r\n',1),
