 (6,'Infraestructura para recreación y esparcimiento','<ul>\r\n	<li><strong>Descripci&oacute;n</strong></li>\r\n</ul>\r\n\r\n<p>Parque Nativo, anfiteatro &nbsp;y Casa de la Cultura</p>\r\n\r\n<ul>\r\n	<li><strong>Dimensiones</strong></li>\r\n</ul>\r\n\r\n<p>2ha de terreno destinado a las actividades recreativas y culturales</p>\r\n\r\n<ul>\r\n	<li><strong>Inversi&oacute;n: 7.000.000</strong></li>\r\n	<li><strong>Origen de los fondos:&nbsp;</strong>Provincia y Naci&oacute;n</li>\r\n</ul>\r\n',1),
 (7,'CEIL','<ul>\r\n	<li><strong>Descripci&oacute;n</strong></li>\r\n</ul>\r\n\r\n<p>Espacio f&iacute;sico para brindar ofertas de formaci&oacute;n en el departamento</p>\r\n\r\n<ul>\r\n	<li><strong>Dimensiones</strong></li>\r\n</ul>\r\n\r\n<p>Edificio de aulas y servicios. Oferta de tecnicaturas, profesorados, post&iacute;tulos, formaci&oacute;n en distintas &aacute;reas</p>\r\n\r\n<ul>\r\n	<li><strong>Origen de los fondos:</strong><strong>&nbsp;</strong><strong>Naci&oacute;n</strong></li>\r\n</ul>\r\n',1),
 (8,'Asfalto','<ul>\r\n	<li><strong>Descripci&oacute;n</strong></li>\r\n</ul>\r\n\r\n<p>Asfalto en Caliente en radios urbanos</p>\r\n\r\n<ul>\r\n	<li><strong>Dimensiones</strong></li>\r\n</ul>\r\n\r\n<p>Cierre de circuitos urbanos en &aacute;rea de Villa y Costa</p>\r\n\r\n<ul>\r\n	<li><strong>Inversi&oacute;n:&nbsp;</strong>25.000.000?</li>\r\n	<li><strong>Origen de los fondos:</strong>&nbsp;Provincial</li>\r\n</ul>\r\n',1),
 (9,'Adquisición de Equipos de Asfalto en Frío','<ul>\r\n	<li><strong>Descripci&oacute;n</strong></li>\r\n</ul>\r\n\r\n<p>Importaci&oacute;n de un Equipo de 7 m&aacute;quinas.</p>\r\n\r\n<ul>\r\n	<li><strong>Dimensiones</strong></li>\r\n</ul>\r\n\r\n<p>Ejecuci&oacute;n de asfaltos en fr&iacute;o sobre caminos enripiados previamente.</p>\r\n\r\n<ul>\r\n	<li><strong>Inversi&oacute;n:</strong>&nbsp;6.000.000</li>\r\n	<li><strong>Origen de los fondos:&nbsp;</strong>Ahorro Municipal.</li>\r\n</ul>\r\n',1),
